deis = buildGoPackage rec {
  name = "deis-${version}";
  version = "1.13.0";

  goPackagePath = "github.com/deis/deis"; 
  subPackages = [ "client" ]; 

  src = fetchFromGitHub {
    owner = "deis";
    repo = "deis";
    rev = "v${version}";
    sha256 = "1qv9lxqx7m18029lj8cw3k7jngvxs4iciwrypdy0gd2nnghc68sw";
  };

  goDeps = ./deps.nix; 

  buildFlags = "--tags release"; 
}

