listitem.programlisting:
{ services.xserver.xrandrHeads = [
    "HDMI-0"
    {
      output = "DVI-0";
      primary = true;
      monitorConfig = ''
        Option "Rotate" "right"
      '';
    }
  ];
}

CREATE USER 'root'@'%' IDENTIFIED BY '';
GRANT ALL PRIVILEGES ON *.* TO 'root'@'%' WITH GRANT OPTION;
FLUSH PRIVILEGES;

